import{_ as t,o as d,c as r,b as l,e as c,r as a}from"./app.34a7315f.js";const f=JSON.parse('{"title":"TCP \u548C UDP \u7684\u533A\u522B","description":"","frontmatter":{},"headers":[{"level":2,"title":"TCP","slug":"tcp","link":"#tcp","children":[]},{"level":2,"title":"TCP \u7684\u7279\u70B9","slug":"tcp-feature","link":"#tcp-feature","children":[]},{"level":2,"title":"UDP","slug":"udp","link":"#udp","children":[]},{"level":2,"title":"UDP \u7279\u70B9","slug":"udp-feature","link":"#udp-feature","children":[]},{"level":2,"title":"TCP\u548CUDP\u7684\u6BD4\u8F83","slug":"compare","link":"#compare","children":[]},{"level":2,"title":"\u53C2\u8003","slug":"refer","link":"#refer","children":[]}],"relativePath":"front/network/tcp-udp.md"}'),o={name:"front/network/tcp-udp.md"},n=c('<h1 id="tcp-\u548C-udp-\u7684\u533A\u522B" tabindex="-1">TCP \u548C UDP \u7684\u533A\u522B <a class="header-anchor" href="#tcp-\u548C-udp-\u7684\u533A\u522B" aria-hidden="true">#</a></h1><p><code>TCP</code> \u548C <code>UDP</code> \u90FD\u662F\u4F20\u8F93\u5C42\u534F\u8BAE\uFF0C\u4ED6\u4EEC\u90FD\u5C5E\u4E8E <code>TCP/IP</code> \u534F\u8BAE\u65CF\u3002</p><h2 id="tcp" tabindex="-1">TCP <a class="header-anchor" href="#tcp" aria-hidden="true">#</a></h2><p>\u4F20\u8F93\u63A7\u5236\u534F\u8BAE\uFF08TCP\uFF0CTransmission Control Protocol\uFF09\u662F\u4E00\u79CD\u9762\u5411\u8FDE\u63A5\u7684\u3001\u53EF\u9760\u7684\u3001\u57FA\u4E8E\u5B57\u8282\u6D41\u7684\u4F20\u8F93\u5C42\u901A\u4FE1\u534F\u8BAE\u3002</p><h2 id="tcp-feature" tabindex="-1">TCP \u7684\u7279\u70B9 <a class="header-anchor" href="#tcp-feature" aria-hidden="true">#</a></h2><ul><li>\u9762\u5411\u8FDE\u63A5</li></ul><p>\u9762\u5411\u8FDE\u63A5\u6307\u7684\u662F\u5728\u53D1\u9001\u6570\u636E\u4E4B\u524D\u5FC5\u987B\u901A\u8FC7\u201C\u4E09\u6B21\u63E1\u624B\u201D\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u8FD9\u6837\u80FD\u591F\u5EFA\u7ACB<strong>\u53EF\u9760</strong>\u8FDE\u63A5\uFF0C\u4E3A\u6570\u636E\u4F20\u8F93\u63D0\u4F9B\u7A33\u5B9A\u7684\u5B57\u8282\u6D41\u670D\u52A1\u3002</p><ul><li>\u4EC5\u652F\u6301\u5355\u64AD\u4F20\u8F93</li></ul><p>\u6BCF\u6761 <code>TCP</code> \u8FDE\u63A5\u90FD\u5FC5\u987B\u662F<strong>\u4E00\u5BF9\u4E00</strong>\u7684\u3002</p><ul><li>\u9762\u5411\u5B57\u8282\u6D41</li></ul><p>\u5B57\u8282\u6D41\u670D\u52A1\u662F\u6307\uFF0C\u4E3A\u4E86\u65B9\u4FBF\u4F20\u8F93\uFF0C\u5C06\u5927\u5757\u6570\u636E\u5206\u5272\u6210\u4EE5\u62A5\u6587\u6BB5 <code>segment</code> \u4E3A\u5355\u4F4D\u7684\u6570\u636E\u5305\u8FDB\u884C\u7BA1\u7406\u3002</p><ul><li>\u53EF\u9760\u4F20\u8F93</li></ul><p>\u5BF9\u4E8E\u53EF\u9760\u4F20\u8F93\uFF0C\u5224\u65AD\u4E22\u5305\uFF0C\u8BEF\u7801\u9760\u7684\u662F\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u53D1\u51FA\u7684 <code>TCP</code> \u5E8F\u53F7\u548C\u786E\u8BA4\u53F7 <code>ACK</code>\u3002<code>TCP</code> \u4E3A\u4E86\u4FDD\u8BC1\u62A5\u6587\u4F20\u8F93\u7684\u53EF\u9760\uFF0C\u5C31\u7ED9\u6BCF\u4E2A\u5305\u4E00\u4E2A\u5E8F\u53F7\uFF0C\u540C\u65F6\u5E8F\u53F7\u4E5F\u4FDD\u8BC1\u4E86\u4F20\u9001\u5230\u63A5\u6536\u7AEF\u5B9E\u4F53\u7684\u5305\u7684\u6309\u5E8F\u63A5\u6536\u3002\u7136\u540E\u63A5\u6536\u7AEF\u5B9E\u4F53\u5BF9\u5DF2\u6210\u529F\u6536\u5230\u7684\u5B57\u8282\u53D1\u56DE\u4E00\u4E2A\u76F8\u5E94\u7684\u786E\u8BA4 <code>ACK</code>\uFF1B\u5982\u679C\u53D1\u9001\u7AEF\u5B9E\u4F53\u5728\u5408\u7406\u7684\u5F80\u8FD4\u65F6\u5EF6 <code>2MSL</code> \u5185\u672A\u6536\u5230\u786E\u8BA4\uFF0C\u90A3\u4E48\u5BF9\u5E94\u7684\u4E22\u5931\u7684\u6570\u636E\u5C06\u4F1A\u88AB\u91CD\u4F20\u3002</p><ul><li>\u62E5\u585E\u63A7\u5236</li></ul><p><code>TCP</code> \u80FD\u591F\u8BA1\u7B97\u5F53\u524D\u7F51\u7EDC\u7684\u4F20\u8F93\u901F\u7387\uFF0C\u6839\u636E\u5B83\u53BB<strong>\u8C03\u6574</strong>\u6570\u636E\u5305\u7684\u4F20\u8F93\u901F\u7387\uFF0C\u4E22\u5305\u6982\u7387\u5C0F\u3002</p><ul><li>TCP \u63D0\u4F9B\u5168\u53CC\u5DE5\u901A\u4FE1</li></ul><p><code>TCP</code> \u5141\u8BB8\u901A\u4FE1\u53CC\u65B9\u7684\u5E94\u7528\u7A0B\u5E8F\u5728\u4EFB\u4F55\u65F6\u5019\u90FD\u80FD\u53D1\u9001\u6570\u636E\u3002<code>TCP</code> \u8FDE\u63A5\u7684\u4E24\u7AEF\u90FD\u8BBE\u6709<strong>\u53D1\u9001\u7F13\u5B58</strong>\u548C<strong>\u63A5\u6536\u7F13\u5B58</strong>\uFF0C\u7528\u6765\u4E34\u65F6\u5B58\u653E\u53CC\u5411\u901A\u4FE1\u7684\u6570\u636E\u3002</p><h2 id="udp" tabindex="-1">UDP <a class="header-anchor" href="#udp" aria-hidden="true">#</a></h2><p>\u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE \uFF08UDP\uFF0CUser Datagram Protocol\uFF09\u662F\u4E00\u79CD\u9762\u5411\u65E0\u8FDE\u63A5\u7684\u534F\u8BAE\u3002</p><h2 id="udp-feature" tabindex="-1">UDP \u7279\u70B9 <a class="header-anchor" href="#udp-feature" aria-hidden="true">#</a></h2><ul><li>\u9762\u5411\u65E0\u8FDE\u63A5</li></ul><p><code>UDP</code> \u4E0D\u50CF <code>TCP</code> \u662F\u57FA\u4E8E\u8FDE\u63A5\u624D\u80FD\u53D1\u9001\u6570\u636E\u7684\uFF0C<code>UDP</code> \u53D1\u9001\u6570\u636E\u4E0D\u9700\u8981\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u6240\u4EE5 <code>UDP</code> \u6BD4 <code>TCP</code> \u7684\u901F\u5EA6\u66F4\u5FEB\u3002</p><ul><li>\u6709\u5355\u64AD\uFF0C\u591A\u64AD\uFF0C\u5E7F\u64AD\u7684\u529F\u80FD</li></ul><p>\u591A\u64AD\u3001\u5E7F\u64AD\u662F\u6307\uFF0C\u670D\u52A1\u5668\u63A5\u6536\u591A\u4E2A\u6570\u636E\u5305\uFF0C\u5176\u6570\u636E\u5305\u5728\u53D1\u9001\u65F6\u4E0D\u9700\u8981\u7B49\u5F85\u56DE\u5E94\uFF0C\u56E0\u6B64\u591A\u64AD\u3001\u5E7F\u64AD\u53D1\u9001\u7684\u6570\u636E\u662F\u4E0D\u7A33\u5B9A\u4E14\u5BB9\u6613\u4E22\u5931\u7684\u3002</p><ul><li>\u9762\u5411\u62A5\u6587</li></ul><p><code>UDP</code> \u53D1\u9001\u6570\u636E\u5305\u65F6\u4E0D\u50CF <code>TCP</code> \u4F1A\u5C06\u6570\u636E\u5305\u62C6\u5206\u6210\u6570\u636E\u7247\u6BB5 <code>segment</code>\uFF0C<code>UDP</code> \u5BF9\u4E8E\u5E94\u7528\u7AEF\u4EA4\u4E0B\u6765\u7684\u62A5\u6587\u65E2\u4E0D\u5408\u5E76\u4E5F\u4E0D\u62C6\u5206\uFF0C\u800C\u662F\u5C06\u62A5\u6587\u4E3B\u4F53\u53D1\u9001\u7ED9\u670D\u52A1\u7AEF\uFF0C\u53D1\u9001\u65F6\uFF0C<code>UDP</code> \u662F\u65E0\u5E8F\u53D1\u9001\u6570\u636E\u5305\uFF0C\u670D\u52A1\u7AEF\u5E76\u4E0D\u77E5\u9053\u54EA\u4E2A\u6570\u636E\u5148\u6765\u3002</p><ul><li>\u4E0D\u53EF\u9760\u6027</li></ul><p>\u9996\u5148\u4E0D\u53EF\u9760\u6027\u4F53\u73B0\u5728\u65E0\u8FDE\u63A5\u4E0A\uFF0C\u901A\u4FE1\u90FD\u4E0D\u9700\u8981\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u60F3\u53D1\u5C31\u53D1\uFF0C\u8FD9\u6837\u7684\u60C5\u51B5\u80AF\u5B9A\u4E0D\u53EF\u9760\u3002</p><p>\u5E76\u4E14\u6536\u5230\u4EC0\u4E48\u6570\u636E\u5C31\u4F20\u9012\u4EC0\u4E48\u6570\u636E\uFF0C\u5E76\u4E14\u4E5F\u4E0D\u4F1A\u5907\u4EFD\u6570\u636E\uFF0C\u53D1\u9001\u6570\u636E\u4E5F\u4E0D\u4F1A\u5173\u5FC3\u5BF9\u65B9\u662F\u5426\u5DF2\u7ECF\u6B63\u786E\u63A5\u6536\u5230\u6570\u636E\u4E86\u3002</p><p>\u518D\u8005\u7F51\u7EDC\u73AF\u5883\u65F6\u597D\u65F6\u574F\uFF0C\u4F46\u662F <code>UDP</code> \u56E0\u4E3A\u6CA1\u6709\u62E5\u585E\u63A7\u5236\uFF0C\u4E00\u76F4\u4F1A\u4EE5\u6052\u5B9A\u7684\u901F\u5EA6\u53D1\u9001\u6570\u636E\u3002\u5373\u4F7F\u7F51\u7EDC\u6761\u4EF6\u4E0D\u597D\uFF0C\u4E5F\u4E0D\u4F1A\u5BF9\u53D1\u9001\u901F\u7387\u8FDB\u884C\u8C03\u6574\u3002\u8FD9\u6837\u5B9E\u73B0\u7684\u5F0A\u7AEF\u5C31\u662F\u5728\u7F51\u7EDC\u6761\u4EF6\u4E0D\u597D\u7684\u60C5\u51B5\u4E0B\u53EF\u80FD\u4F1A\u5BFC\u81F4\u4E22\u5305\uFF0C\u4F46\u662F\u4F18\u70B9\u4E5F\u5F88\u660E\u663E\uFF0C\u5728\u67D0\u4E9B\u5B9E\u65F6\u6027\u8981\u6C42\u9AD8\u7684\u573A\u666F\uFF08\u6BD4\u5982\u76F4\u64AD\u3001\u89C6\u9891\u4F1A\u8BAE\uFF09\u5C31\u9700\u8981\u4F7F\u7528 <code>UDP</code> \u800C\u4E0D\u662F <code>TCP</code>\u3002</p><ul><li>\u4F20\u8F93\u6570\u636E\u9AD8\u6548\u6027</li></ul><p><code>UDP</code> \u7684\u5934\u90E8\u5F00\u9500\u5C0F\uFF0C\u53EA\u6709<strong>8\u5B57\u8282</strong>\uFF0C\u76F8\u6BD4 <code>TCP</code> \u7684\u81F3\u5C11<strong>20-60\u5B57\u8282</strong>\u8981\u5C11\u5F97\u591A\uFF0C\u5728\u4F20\u8F93\u6570\u636E\u62A5\u6587\u65F6\u662F\u5F88\u9AD8\u6548\u7684</p><h2 id="compare" tabindex="-1">TCP\u548CUDP\u7684\u6BD4\u8F83 <a class="header-anchor" href="#compare" aria-hidden="true">#</a></h2><table><thead><tr><th style="text-align:center;">\u5BF9\u6BD4</th><th style="text-align:center;">TCP</th><th style="text-align:center;">UDP</th></tr></thead><tbody><tr><td style="text-align:center;">\u8FDE\u63A5\u65B9\u5F0F</td><td style="text-align:center;">\u9762\u5411\u8FDE\u63A5</td><td style="text-align:center;">\u65E0\u8FDE\u63A5</td></tr><tr><td style="text-align:center;">\u662F\u5426\u53EF\u9760</td><td style="text-align:center;">\u53EF\u9760\u4F20\u8F93\uFF0C\u4F7F\u7528\u6D41\u91CF\u63A7\u5236\u548C\u62E5\u585E\u63A7\u5236</td><td style="text-align:center;">\u4E0D\u53EF\u9760\u4F20\u8F93\uFF0C\u4E0D\u4F7F\u7528\u6D41\u91CF\u63A7\u5236\u548C\u62E5\u585E\u63A7\u5236</td></tr><tr><td style="text-align:center;">\u8FDE\u63A5\u5BF9\u8C61\u4E2A\u6570</td><td style="text-align:center;">\u53EA\u80FD\u662F\u4E00\u5BF9\u4E00\u901A\u4FE1</td><td style="text-align:center;">\u652F\u6301\u4E00\u5BF9\u4E00\uFF0C\u4E00\u5BF9\u591A\uFF0C\u591A\u5BF9\u4E00\u548C\u591A\u5BF9\u591A\u4EA4\u4E92\u901A\u4FE1</td></tr><tr><td style="text-align:center;">\u9996\u90E8\u5F00\u9500</td><td style="text-align:center;">\u9996\u90E8\u6700\u5C0F20\u5B57\u8282\uFF0C\u6700\u592760\u5B57\u8282</td><td style="text-align:center;">\u9996\u90E8\u5F00\u9500\u5C0F\uFF0C\u4EC58\u5B57\u8282</td></tr><tr><td style="text-align:center;">\u9002\u7528\u573A\u666F</td><td style="text-align:center;">\u9002\u7528\u4E8E\u8981\u6C42\u53EF\u9760\u4F20\u8F93\u7684\u5E94\u7528\uFF0C\u4F8B\u5982\u6587\u4EF6\u4F20\u8F93</td><td style="text-align:center;">\u76F4\u64AD\uFF0C\u89C6\u9891\u804A\u5929</td></tr></tbody></table><h2 id="refer" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#refer" aria-hidden="true">#</a></h2><p><a href="https://www.spiceworks.com/tech/networking/articles/tcp-vs-udp/" target="_blank" rel="noreferrer">tcp-vs-udp</a><br><a href="https://github.com/yuanyuanbyte/Blog/issues/121" target="_blank" rel="noreferrer">TCP \u548C UDP \u7684\u7279\u70B9\u3001\u533A\u522B\u548C\u4F7F\u7528\u573A\u666F</a></p>',36);function i(s,p,h,u,P,g){const e=a("TheEnd");return d(),r("div",null,[n,l(e)])}const C=t(o,[["render",i]]);export{f as __pageData,C as default};
