import{_ as o,o as c,c as d,b as a,e as i,r as l}from"./app.503ed92f.js";const f=JSON.parse('{"title":"Vue3 \u63D0\u5347","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u6E90\u7801\u4F18\u5316","slug":"source-advance","link":"#source-advance","children":[]},{"level":2,"title":"\u6027\u80FD\u4F18\u5316","slug":"performance-advance","link":"#performance-advance","children":[]},{"level":2,"title":"\u7F16\u8BD1\u4F18\u5316","slug":"complier-advance","link":"#complier-advance","children":[]},{"level":2,"title":"\u8BED\u6CD5\u4F18\u5316","slug":"syntax-advance","link":"#syntax-advance","children":[]}],"relativePath":"front/vue/vue3-advance.md"}'),n={name:"front/vue/vue3-advance.md"},r=i('<h1 id="vue3-\u63D0\u5347" tabindex="-1">Vue3 \u63D0\u5347 <a class="header-anchor" href="#vue3-\u63D0\u5347" aria-hidden="true">#</a></h1><p><code>Vue3</code> \u7684\u63D0\u5347\u76F8\u8F83\u4E8E <code>Vue2</code> \u4E3B\u8981\u4F53\u73B0\u57284\u4E2A\u65B9\u9762\uFF1A</p><ul><li>\u6E90\u7801\u4F18\u5316</li><li>\u6027\u80FD\u4F18\u5316</li><li>\u7F16\u8BD1\u4F18\u5316</li><li>\u8BED\u6CD5\u4F18\u5316</li></ul><p>\u63A5\u4E0B\u6765\u56F4\u7ED5\u8FD9\u51E0\u70B9\u6765\u7B80\u8FF0 <code>Vue3</code> \u7684\u7A81\u51FA\u4F18\u52BF\u3002</p><h2 id="source-advance" tabindex="-1">\u6E90\u7801\u4F18\u5316 <a class="header-anchor" href="#source-advance" aria-hidden="true">#</a></h2><p>\u4EE3\u7801\u6574\u4F53\u67B6\u6784\u91C7\u7528 <code>monorepo</code> \u548C <code>TypeScript</code> \u6765\u7BA1\u7406\u548C\u5F00\u53D1\u6E90\u7801\uFF0C\u5B83\u7684\u76EE\u7684\u662F\u8BA9\u4EE3\u7801\u66F4\u6613\u4E8E\u5F00\u53D1\u548C\u7EF4\u62A4</p><ol><li><p>\u76F8\u5BF9\u4E8E <code>Vue2.x</code> \u7684\u6E90\u7801\u7EC4\u7EC7\u65B9\u5F0F\uFF0C<code>monorepo</code> \u628A\u8FD9\u4E9B\u6A21\u5757\u62C6\u5206\u5230\u4E0D\u540C\u7684 <code>package</code> \u4E2D\uFF0C\u6BCF\u4E2A <code>package</code> \u6709\u5404\u81EA\u7684 <code>API</code>\u3001\u7C7B\u578B\u5B9A\u4E49\u548C\u6D4B\u8BD5\u8FD9\u6837\u4F7F\u5F97\u6A21\u5757\u62C6\u5206\u66F4\u7EC6\u5316\uFF0C\u804C\u8D23\u5212\u5206\u66F4\u660E\u786E\uFF0C\u6A21\u5757\u4E4B\u524D\u7684\u4F9D\u8D56\u5173\u7CFB\u4E5F\u66F4\u52A0\u660E\u786E\u3002</p></li><li><p><code>package</code>\uFF08\u6BD4\u5982 <code>reactivity</code> \u54CD\u5E94\u5F0F\u5E93\uFF09\u662F\u53EF\u4EE5\u72EC\u7ACB\u4E8E <code> Vue.js</code> \u4F7F\u7528\u7684\uFF0C\u8FD9\u6837\u7528\u6237\u5982\u679C\u53EA\u60F3\u4F7F\u7528 <code>Vue3</code> \u7684\u54CD\u5E94\u5F0F\u80FD\u529B\uFF0C\u53EF\u5355\u72EC\u4F9D\u8D56\u8FD9\u4E2A\u54CD\u5E94\u5F0F\u5E93\u800C\u4E0D\u7528\u53BB\u4F9D\u8D56\u6574\u4E2A <code>Vue.js</code> \u51CF\u5C0F\u4E86\u5F15\u7528\u5305\u7684\u4F53\u79EF\u5927\u5C0F\uFF0C\u800C <code>Vue2</code> \u662F\u505A\u4E0D\u5230\u8FD9\u70B9\u7684\u3002</p></li><li><p>\u4F7F\u7528 <code>TypeScript</code> \u907F\u514D\u65F6\u5019 <code>Type</code> \u7C7B\u578B\u51FA\u73B0\u7684 <code>Bug</code>\uFF0C\u76F8\u8F83\u4E8E <code>Vue2.x</code> \u4F7F\u7528\u7684 <code>Flow.js</code>\uFF0C<code>Ts</code> \u80FD\u591F\u63D0\u4F9B\u66F4\u597D\u7684\u7C7B\u578B\u63A8\u5BFC\u80FD\u529B\u3002</p></li></ol><h2 id="performance-advance" tabindex="-1">\u6027\u80FD\u4F18\u5316 <a class="header-anchor" href="#performance-advance" aria-hidden="true">#</a></h2><ol><li><p>\u79FB\u9664\u51B7\u95E8 <code>Feature</code>\uFF0C\u6BD4\u5982\uFF1A<code>filter</code>\u3001<code>inline-template</code> \u7B49\u3002</p></li><li><p>\u5F15\u5165 <code>tree-shaking</code> \u6280\u672F\uFF1A\u4F9D\u8D56 <code>ES2015</code> \u6A21\u5757\u8BED\u6CD5\u7684\u9759\u6001\u7ED3\u6784\uFF08\u5373 <code>import</code> \u548C <code>export</code>\uFF09\u901A\u8FC7\u7F16\u8BD1\u9636\u6BB5\u7684\u9759\u6001\u5206\u6790\uFF0C\u627E\u5230\u6CA1\u6709\u5F15\u5165\u7684\u6A21\u5757\u5E76\u6253\u4E0A\u6807\u8BB0\u3002</p></li><li><p>\u6570\u636E\u52AB\u6301\u4F18\u5316\uFF1A\u820D\u5F03 <code>ObjectDefineProperty</code>\uFF0C\u4F7F\u7528 <code>Proxy</code>\u3002<br><code>ObjectDefineProperty</code> \u672C\u8EAB\u5B58\u5728\u4E24\u70B9\u95EE\u9898\uFF1A</p><ul><li>\u65E0\u6CD5\u62E6\u622A\u6570\u7EC4\u7D22\u5F15\u65B0\u589E\u548C\u5BF9\u8C61\u5C5E\u6027\u65B0\u589Evalue</li><li>\u5BF9\u4E8E\u6DF1\u5C42\u7EA7\u7684\u5BF9\u8C61\u7ED3\u6784\uFF0C\u8981\u5BF9\u5E95\u90E8\u7684\u5C5E\u6027\u8FDB\u884C\u52AB\u6301\u5C31\u9700\u8981\u91C7\u53D6\u6DF1\u5EA6\u9012\u5F52\u7684\u65B9\u5F0F\u628A\u6BCF\u4E00\u5C42\u7684\u6570\u636E\u90FD\u5B9A\u4E49\u6210\u54CD\u5E94\u5F0F\u6570\u636E\uFF0C\u5982\u679C\u6570\u636E\u8FC7\u4E8E\u590D\u6742\uFF0C\u90A3\u4E48\u5C31\u6709\u76F8\u5F53\u5927\u6027\u80FD\u8D1F\u62C5</li></ul><p><code>Proxy</code> \u7684\u4F18\u52BF</p><p>\u5B83\u53EA\u4F1A\u52AB\u6301\u6574\u4E2A\u5BF9\u8C61\uFF0C\u6CE8\u610F\u7684\u662F\uFF0C<code>Proxy API</code> \u5E76\u4E0D\u80FD\u76D1\u542C\u5230\u5185\u90E8\u6DF1\u5C42\u6B21\u7684\u5BF9\u8C61\u53D8\u5316\uFF0C\u56E0\u6B64 <code>Vue3</code> \u7684\u5904\u7406\u65B9\u5F0F\u662F <strong>\u5728 getter \u4E2D\u53BB\u9012\u5F52\u54CD\u5E94\u5F0F</strong>\uFF0C\u8FD9\u6837\u7684\u597D\u5904\u662F\u771F\u6B63\u8BBF\u95EE\u5230\u5185\u90E8\u5BF9\u8C61\u624D\u4F1A\u53D8\u6210\u54CD\u5E94\u5F0F\uFF0C\u800C\u4E0D\u662F\u65E0\u8111\u9012\u5F52\uFF0C\u8FD9\u6837\u65E0\u7591\u4E5F\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u63D0\u5347\u4E86\u6027\u80FD\u3002</p></li></ol><h2 id="complier-advance" tabindex="-1">\u7F16\u8BD1\u4F18\u5316 <a class="header-anchor" href="#complier-advance" aria-hidden="true">#</a></h2><ol><li><p><code>patch</code> \u4F18\u5316\uFF0C\u5728 <code>Vue2</code> \u4E2D\u6240\u4EE5\u4E00\u4E2A\u7EC4\u4EF6\u4E00\u65E6\u6709\u4E86\u52A8\u6001\u8282\u70B9\u53D8\u52A8\uFF0C\u90A3\u4E48\u5C31\u4F1A <code>patch</code> \u6574\u4E2A\u7EC4\u4EF6\u6811\uFF0C\u7406\u60F3\u72B6\u6001\u4E0B\u53EA\u9700\u904D\u5386\u7ED1\u5B9A\u52A8\u6001\u8282\u70B9\u7684 <code>Vnode</code> \u5C31\u53EF\u4EE5\u3002<code>Vue3</code> \u53EF\u4EE5\u901A\u8FC7\u7F16\u8BD1\u9636\u6BB5\u5BF9\u9759\u6001\u6A21\u7248\u7684\u5206\u6790\uFF0C\u7F16\u8BD1\u751F\u6210\u4E86<strong>Block tree</strong>\u3002<code>Block tree</code> \u662F\u5C06\u4E00\u4E2A\u6A21\u5757\u57FA\u4E8E<strong>\u52A8\u6001\u8282\u70B9</strong>\u6307\u4EE4\u5207\u5272\u7684\u5D4C\u5957\u533A\u5757\uFF0C\u6BCF\u4E2A\u533A\u5757\u5185\u90E8\u7684\u8282\u70B9\u7ED3\u6784\u662F\u56FA\u5B9A\u7684\u6BCF\u4E2A\u533A\u5757\u53EA\u9700\u4EE5\u4E00\u4E2A <code>Array</code> \u6765\u8FFD\u8E2A\u81EA\u8EAB\u5305\u542B\u7684\u52A8\u6001\u8282\u70B9\uFF0C\u501F\u52A9 <code>Block tree</code>\uFF0C\u5C06 <code>vnode</code> \u66F4\u65B0\u6027\u80FD\u7531\u4E0E\u6A21\u7248\u6574\u4F53\u5927\u5C0F\u76F8\u5173\u63D0\u5347\u4E3A\u4E0E\u52A8\u6001\u5185\u5BB9\u7684\u6570\u91CF\u76F8\u5173</p></li><li><p>slot\u7F16\u8BD1\u4F18\u5316</p></li><li><p>\u4E8B\u4EF6\u4FA6\u542C\u51FD\u6570\u7684\u7F13\u5B58\u4F18\u5316</p></li><li><p>\u5728\u8FD0\u884C\u65F6\u91CD\u5199\u4E86 diff \u7B97\u6CD5</p></li></ol><h2 id="syntax-advance" tabindex="-1">\u8BED\u6CD5\u4F18\u5316 <a class="header-anchor" href="#syntax-advance" aria-hidden="true">#</a></h2><ol><li>\u65B0\u589E <code>Composition API</code></li></ol><p><code>Vue2</code> \u7684 <code>OptionsAPI</code>\uFF0C\u5F53\u7EC4\u4EF6\u5C0F\u7684\u65F6\u5019\uFF0C\u8FD9\u79CD\u5206\u7C7B\u65B9\u5F0F\u4E00\u76EE\u4E86\u7136\uFF0C\u4F46\u5728\u5927\u578B\u7EC4\u4EF6\u4E2D\uFF0C\u4E00\u4E2A\u7EC4\u4EF6\u53EF\u80FD\u6709\u591A\u4E2A\u903B\u8F91\u5173\u6CE8\u70B9\uFF0C\u4F46\u4F7F\u7528 <code>Optiopns API</code> \u7684\u65F6\u5019\uFF0C\u6BCF\u4E00\u4E2A\u5173\u6CE8\u70B9\u90FD\u6709\u81EA\u5DF1\u7684 <code>Options</code>\uFF0C\u5982\u679C\u9700\u8981\u4FEE\u6539\u4E00\u4E2A\u903B\u8F91\u5173\u6CE8\u70B9\uFF0C\u5C31\u9700\u8981\u5728\u5355\u4E2A\u6587\u4EF6\u4E2D\u4E0D\u65AD\u4E0A\u4E0B\u5207\u6362\u548C\u5BFB\u627E\uFF0C\u5F00\u53D1\u7684\u4F53\u9A8C\u4E0D\u4F1A\u5F88\u597D\u3002<code>Vue3</code> \u4E3B\u8981\u662F\u63D0\u4F9B\u4E86 <code>Composition API</code>\uFF0C\u89E3\u51B3\u4E86\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5B83\u5C06\u67D0\u4E2A\u903B\u8F91\u5173\u6CE8\u70B9\u76F8\u5173\u7684\u4EE3\u7801\u5168\u90E8\u653E\u5728\u4E00\u4E2A\u51FD\u6570\u91CC\uFF0C\u8FD9\u6837\u5F53\u9700\u8981\u4FEE\u6539\u4E00\u4E2A\u529F\u80FD\u65F6\uFF0C\u5C31\u4E0D\u518D\u9700\u8981\u5728\u6587\u4EF6\u4E2D\u201C\u4E0A\u4E0B\u6A2A\u8DF3\u201D\u4E86\u3002</p><ol start="2"><li><p><code>mixin</code> \u547D\u540D\u51B2\u7A81\u548C\u6570\u636E\u6765\u6E90\u4E0D\u6E05\u6670\uFF0C<code>Composition API</code> \u4E5F\u5E2E\u6211\u4EEC\u89E3\u51B3\u4E86\u6211\u4EEC\u7684\u95EE\u9898\u3002</p></li><li><p>\u66F4\u597D\u7684\u7C7B\u578B\u652F\u6301\uFF0C\u5728\u8C03\u7528\u51FD\u6570\u65F6\uFF0C\u6240\u6709\u7684\u7C7B\u578B\u5C31\u88AB\u63A8\u5BFC\u51FA\u6765\u4E86\uFF0C\u4E0D\u50CF <code>OptionsAPI</code> \u6240\u6709\u4E1C\u897F\u90FD\u4F7F\u7528 <code>this</code>\u3002</p></li><li><p><code>Compostion API</code> \u5BF9 <code>tree-shaking</code> \u66F4\u53CB\u597D\uFF0C\u4EE3\u7801\u4E5F\u66F4\u5BB9\u6613\u538B\u7F29\u3002</p></li></ol>',15);function t(p,s,u,h,v,m){const e=l("TheEnd");return c(),d("div",null,[r,a(e)])}const V=o(n,[["render",t]]);export{f as __pageData,V as default};
